import React, {useState, useEffect} from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { getBookList } from '../slices/BookSlice';
import { Oval } from 'react-loader-spinner';

import ListView from '../components/ListView';
//import ImageView from '../components/ImageView';

import style from '../assets/scss/style.module.scss';

const BookPage = ({query}) => {
    const [page, setPage] = useState(1);

    const { rt, rtmsg, item, loading } = useSelector((state) => state.book);

    const dispatch = useDispatch();

    React.useEffect(() => {
        setPage(1);
        dispatch(getBookList({query: query, page: page}));
    }, [query]);

    return (
        <div>
            {loading && (
                <Oval 
                    color='#ff6600'
                    height={100}
                    width={100}
                    wrapperStyle={{
                        position: 'absolute',
                        left: '50%',
                        top: '50%',
                        marginLeft: '-50%',
                        marginTop: '-50%'
                    }}
                />
            )}

            {rt !== 200 ? (
                <div className={style.errmsg}>
                    <h3>{rt} Error</h3>
                    <p>{rtmsg}</p>
                </div>
            ) : (
                <ListView documents={item.documents} thumb={true} />
            )}

            {/* {rt !== 200 ? (
                <div className={style.errmsg}>
                    <h3>{rt} Error</h3>
                    <p>{rtmsg}</p>
                </div>
            ) : (
                <ImageView documents={item.documents} />
            )} */}
        </div>
    );
};

export default BookPage;
